export const isDeprecatedImportConfig = config => {
  return Object.values(config)[0].message !== undefined || Object.values(config)[0].importSpecifiers !== undefined;
};

// Checks if the value of object is an array, will need to update this method if we add other deprecated types with array values
export const isDeprecatedJSXAttributeConfig = config => {
  return Array.isArray(Object.values(config)[0]);
};

