// A scroll event will only be triggered when there is a value of at least 1px change
export const minScroll = 1;
export const vertical = {
  direction: 'vertical',
  start: 'top',
  end: 'bottom',
  size: 'height',
  scrollAxis: 'scrollTop'
};
export const horizontal = {
  direction: 'horizontal',
  start: 'left',
  end: 'right',
  size: 'width',
  scrollAxis: 'scrollLeft'
};
export const defaultAllowedAxis = [horizontal.direction, vertical.direction];