/**
 * @example
 * ```tsx
 * // this is the search comment
 * const findThisNode = 10
 * ^^^^^^^^^^^^^^^^^^^^^^^
 * ```
 */
export function getNodeAfterComment(source, comment) {
    const token = source.getTokenAfter(comment);
    if (token) {
        return source.getNodeByRangeIndex(token.range[1]);
    }
    return null;
}
